import React from 'react'
import { useSelector } from 'react-redux'



export function ChatMessages({ roomId, username }) {

    const rooms = useSelector(state => state.rooms.rooms)

    const room = rooms.find(room => room.id === roomId)
    const roomChatMessages = room.messages

    const renderedMessages = roomChatMessages.map(message => (
        <div className="list-group-item w-75 mx-auto float-start" key={message.id}>
            <div className="fw-bold">{message.username}</div>
            <div className="text-muted">{message.text}</div>
        </div>
    ))

    return (
        <div className="card-body list-group list-group-flush mt-2 chat-messages">
            {renderedMessages}
        </div>
    )
}
