import React, { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux'
import './App.css';

import { io } from 'socket.io-client'

import { nanoid } from '@reduxjs/toolkit';

import { RoomHeader } from './features/rooms/RoomHeader'
import { RoomsList } from './features/rooms/RoomsList'
import { RoomFooter } from './features/rooms/RoomFooter'

import { ChatHeader } from './features/chats/ChatHeader'
import { ChatMessages } from './features/chats/ChatMessages'
import { ChatInput } from './features/chats/ChatInput'

import { newChatMessage, newRoomCreated } from './features/rooms/roomsSlice'

let socket;
const endpoint = 'http://localhost:3001'

const colorChoices = [
  'text-primary', 'text-success', 'text-secondary', 'text-danger',
  'text-warning', 'text-info', 'text-dark',
]

let getRandomInt = (min, max) => (
  Math.floor(Math.random() * (max - min) + min)
)

let randomInt = getRandomInt(0, colorChoices.length + 1)

const App = () => {
  
  const [clicked, setClicked] = useState(false);
  const [roomId, setRoomId] = useState('');
  const [username, setUsername] = useState('');
  const [usernameColor, setUsernameColor] = useState('');

  const rooms = useSelector(state => state.rooms.rooms);
  const dispatch = useDispatch();

  
  useEffect(() => {
    socket = io(endpoint);
    socket.on('chat message', function(data) {
      dispatch(newChatMessage({
        roomId: data.roomId,
        text: data.text,
        messageId: nanoid(),
        username: data.username,
        usernameColor: data.usernameColor
      }));
    });
    
    socket.on('new room created', function(data) {
      dispatch(newRoomCreated({
        roomId: data.roomId,
        roomName: data.roomName
      }));

      if (data.username === username) {
        const room_id = data.roomId
        const room = rooms.find(room => room.id === room_id)
        alert(room.name)
        // setClicked(true)
        // setRoomId(data.roomId)
      }
    })
   
  }, [dispatch, username, rooms]);

  const handleClick = (e, roomId) => {
    setClicked(true)
    setRoomId(roomId)
  }

  const handleUsername = (username) => {
    setUsername(username)
    const username_color = colorChoices[randomInt]
    setUsernameColor(username_color)
  }

  const emitMessage = (text, roomId, username, usernameColor) => {
    if (text) {
      socket.emit('chat message', { text, roomId, username, usernameColor })
    }
  }

  const handleBackToRoomDisplay = () => {
    setClicked(false)
  }

  const sendUserToChatDisplay = (roomName, roomId, username) => {
    // const room = useSelector(state => state.rooms.rooms.find(room => room.name === newRoomName))
    alert(roomName + ' ' + roomId)
    socket.emit('new room created', { roomName, roomId, username })
    // const room = rooms.find(room => room.name === newRoomName)
    // const roomId = room.id
    // setRoomId(roomId)
    // setRoomId(roomId)
    // setClicked(true)
    
    
  }


  const renderedRooms = rooms.map(room => (
      <tr key={room.id}>
        <td>
          <div className="d-flex btn" onClick={e => handleClick(e, room.id)}>
            <div className="room-name">{room.name}</div>
          </div>          
        </td>
      </tr>
  ));
  
  return (
    <div className="container mt-3 app-container">
    {username ? 
      clicked ?
        <ChatDisplay
          roomId={roomId}
          emitMessage={emitMessage}
          handleClick={handleClick}
          username={username}
          usernameColor={usernameColor}
          handleBackToRoomDisplay={handleBackToRoomDisplay}
        /> :
        <RoomDisplay 
          renderedRooms={renderedRooms}
          username={username}
          sendUserToChatDisplay={sendUserToChatDisplay}
        />
      :
      <UserEntry handleUsername={handleUsername}/>
    }
    </div>   

    // <div className="container w-50 mt-5">
    //   { clicked ?
    //     <ChatDisplay 
    //       roomId={roomId}
    //       emitMessage={emitMessage}
    //       handleClick={handleClick}
    //     /> :
    //     <RoomDisplay renderedRooms={renderedRooms} />
    //   }
    // </div>
  )
}

function UserEntry({ handleUsername }) {
  const [username, setUsername] = useState('')

  const handleChange = (e) => {
    setUsername(e.target.value)    
  }
  const handleSubmit = () => {
    if (username) {
      handleUsername(username)
    }
  }

  return (
    <div className="position-absolute top-50 start-50 translate-middle">
      <div className="card text-white bg-dark mb-3">
        <div className="card-header">Enter a username</div>        
        <div className="card-body input-group">
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <input 
                className="form-control"
                type="text"
                value={username}
                onChange={handleChange}
              />
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}

function ChatDisplay({ roomId, emitMessage, handleClick, username, handleBackToRoomDisplay, usernameColor }) {   
  return (
    <div className="card chat-display">
      <div className="card-body">
        <ChatHeader 
          roomId={roomId} 
          handleClick={handleClick}
          handleBackToRoomDisplay={handleBackToRoomDisplay}
        />
        <ChatMessages 
          roomId={roomId}
          username={username}
          usernameColor={usernameColor}          
        />
        <ChatInput 
          roomId={roomId}
          emitMessage={emitMessage}
          username={username}
          usernameColor={usernameColor}       
        />
      </div>
    </div>
  );
};

function RoomDisplay({ renderedRooms, username, sendUserToChatDisplay }) {
  return (
    <div className="card">
      <div className="card-body">
        <RoomHeader 
          username={username}
        />
        <RoomsList          
          renderedRooms={renderedRooms} 
          sendUserToChatDisplay={sendUserToChatDisplay} 
          username={username}          
        />
        <RoomFooter />
      </div>
    </div>
  )
}

export default App;
