import { createSlice, nanoid } from '@reduxjs/toolkit'

const initialState = {
    rooms: [
        { id: "1", name: "IT", messages: [{ id: nanoid(), text: "hey sup" }]},
        { id: "2", name: "Politics", messages: [{ id: nanoid(), text: "hello" }]},
        { id: "3", name: "Football", messages: [{ id: nanoid(), text: "hii" }]}
    ],
    status: 'idle',
    error: null
}

const roomsSlice = createSlice({
    name: 'rooms',
    initialState,
    reducers: {
        newChatMessage(state, action) {
            const { roomId, text, messageId } = action.payload
            const currentRoom = state.rooms.find(room => room.id === roomId)
            if (currentRoom) {
                currentRoom.messages.push({
                    id: messageId,
                    text: text
                })
            }
        },
    },
})

export const newChatMessage = roomsSlice.actions

export default roomsSlice.reducer
