import React, { useState } from 'react'

export function RoomsList({ renderedRooms }) {
    const [clicked, setClicked] = useState(false)

    const handleClick = () => {
        setClicked(true)
    }

    const displayRoomInput = (
        <div className="">
            <form>
                <div className="input-group">
                    <input className="form-control" type="text" />
                </div>
            </form>
        </div>
    )

    const displayCreateRoomButton = (
        <tr>
            <td>
                <div 
                    className="d-flex btn fw-bold"
                    onClick={handleClick}
                >
                    <div>Create a room</div>
                    <div className="ms-2 fw-bold"><i className="bi bi-plus"></i></div>
                </div>          
            </td>
        </tr>
    )

    return (
        <div className="rooms-list bg-light mt-2">
            <table className="table">
                <tbody>
                    {renderedRooms}
                    {clicked ?
                        displayRoomInput :
                        displayCreateRoomButton
                    }                    
                </tbody>
            </table>            
        </div>
    )
}
