import { createSlice, nanoid } from '@reduxjs/toolkit'


const initialState = {
    rooms: [
        { id: "1", name: "IT", messages: [{ id: nanoid(), text: "Welcome to IT Room", username: { name: 'admin', color: '' }}]},
        { id: "2", name: "Politics", messages: [{ id: nanoid(), text: "Welcome to Politics Room", username: { name: 'admin', color: '' }}]},
        { id: "3", name: "Football", messages: [{ id: nanoid(), text: "Welcome to Football Room", username: { name: 'admin', color: '' }}]}
    ],
    status: 'idle',
    error: null
}

const roomsSlice = createSlice({
    name: 'rooms',
    initialState,
    reducers: {
        newRoomCreated(state, action) {
            const { id, roomName } = action.payload
            state.rooms.push({
                id: id,
                name: roomName,
                messages: []
            })
        },

        newChatMessage(state, action) {
            const { roomId, text, messageId, username } = action.payload
            const currentRoom = state.rooms.find(room => room.id === roomId)
            if (currentRoom) {
                currentRoom.messages.push({
                    id: messageId,
                    text: text,
                    username: username,                    
                })
            }
        },
    }
})

export const { newChatMessage, newRoomCreated } = roomsSlice.actions

export default roomsSlice.reducer
