import React, { useState } from 'react'
import { OverlayTrigger, Popover } from 'react-bootstrap'
import { 
    EmojiSmile, EmojiExpressionless, EmojiDizzy, EmojiAngry, EmojiLaughing,
    EmojiSunglasses, EmojiWink, EmojiWinkFill, EmojiSmileUpsideDown, EmojiSmileUpsideDownFill,
    EmojiHeartEyes, EmojiHeartEyesFill, EmojiFrown, EmojiNeutral, EmojiNeutralFill,
} from 'react-bootstrap-icons'

export const ChatInput = ({ roomId, emitMessage, username }) => {

    const [chatMessage, setChatMessage] = useState('');

    const handleChange = (e) => {
        setChatMessage(e.target.value);       
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        if (chatMessage) {            
            emitMessage(chatMessage, roomId, username)
            setChatMessage('');
        };

    };

    // const emojis = [
    //     Eject, EmojiSmile, EmojiExpressionless, EmojiDizzy, EmojiAngry, EmojiLaughing,
    //     EmojiSunglasses, EmojiWink, EmojiWinkFill, EmojiSmileUpsideDown, EmojiSmileUpsideDownFill,
    //     EmojiHeartEyes, EmojiHeartEyesFill, EmojiFrown, EmojiNeutral, EmojiNeutralFill,
    // ];


    const popover = (
        <Popover id="popover-emoji">
            <Popover.Title as="h4">Emojis</Popover.Title>
            <Popover.Content>
                <EmojiSmile className="me-1" />
                <EmojiExpressionless className="me-1" />
                <EmojiDizzy className="me-1" />
                <EmojiAngry className="me-1" />
                <EmojiLaughing className="me-1" />
                <EmojiSunglasses className="me-1" />
                <EmojiWink className="me-1" />
                <EmojiWinkFill className="me-1" />
                <EmojiSmileUpsideDown className="me-1" />
                <EmojiSmileUpsideDownFill className="me-1" />
                <EmojiHeartEyes className="me-1" />
                <EmojiHeartEyesFill className="me-1" />
                <EmojiFrown className="me-1" />
                <EmojiNeutral className="me-1" />
                <EmojiNeutralFill className="me-1" />

            </Popover.Content>
        </Popover>
    );

    return (
        <div>
            <div className="text-muted mt-2 ms-2 fs-6 mb-0">say something...</div>
            <hr />
            <form onSubmit={handleSubmit}>
                <div className="input-group">
                    <OverlayTrigger trigger="click" placement="top" overlay={popover}>
                        <button className="btn emoji-popover" type="button">
                            <i className="bi bi-emoji-smile"></i>
                        </button>
                    </OverlayTrigger>                    
                    <input
                        className="chat-input form-control border-0 "
                        type="text"
                        value={chatMessage}
                        placeholder=""
                        onChange={handleChange}
                    />
                    <button 
                        className="chat-input-button btn text-muted"
                        type="submit"
                        // value="Send"
                        onClick={handleSubmit}
                    >
                        <i className="bi bi-cursor-fill"></i>
                    </button>
                </div>
            </form>
            <hr />
        </div>
    );
}
