import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { newRoom } from './roomsSlice'

export function RoomsList({ renderedRooms, sendUserToChatDisplay }) {
    const [clicked, setClicked] = useState(false)
    const [newRoomName, setNewRoomName] = useState('') 
    const dispatch = useDispatch()

    const handleClick = () => {
        setClicked(true)
    }

    const handleChange = (e) => {
        setNewRoomName(e.target.value);
    }

    const submitForm = (e) => {
        e.preventDefault();
        if (newRoomName) {
            dispatch(newRoom({
                roomName: newRoomName
            }));

            sendUserToChatDisplay();
        }
        setNewRoomName('')
    }

    const displayRoomInput = (
        <tr>
            <td>
                <div className="">
                    <form onSubmit={submitForm}>
                        <div className="input-group">
                            <input 
                                className="form-control" 
                                type="text" 
                                value={newRoomName}
                                placeholder="Choose a name ..."
                                onChange={handleChange}
                            />
                        </div>
                    </form>
                </div>
            </td>
        </tr>        
    )

    const displayCreateRoomButton = (
        <tr>
            <td>
                <div className="d-flex btn fw-bold" onClick={handleClick}>
                    <div>Create a room</div>
                    <div className="ms-2 fw-bold">
                        <i className="bi bi-plus"></i>
                    </div>
                </div>          
            </td>
        </tr>
    )

    return (
        <div className="rooms-list bg-light mt-2">
            <table className="table">
                <tbody>
                    {renderedRooms}
                    {clicked ?
                        displayRoomInput :
                        displayCreateRoomButton
                    }                    
                </tbody>
            </table>            
        </div>
    )
}
