import React from 'react'
import { useSelector } from 'react-redux'



export function ChatMessages({ roomId, username }) {

    const rooms = useSelector(state => state.rooms.rooms)

    const room = rooms.find(room => room.id === roomId)
    const roomChatMessages = room.messages

    const renderedMessages = roomChatMessages.map(message => (
        <div className="row p-2 w-75 text-wrap text-break" key={message.id}>
            { username === message.username ? 
                <div className="col fw-bold">{message.username}</div> :
                <div className="col fw-bold text-success">{message.username}</div>}         

            <div className="col-10 text-muted">{message.text}</div>
        </div>
    ))

    return (
        <div className="mt-2 chat-messages bg-light">
            {renderedMessages}
        </div>
    )
}
