import React, { useState } from 'react'

export const ChatInput = ({ roomId, emitMessage }) => {

    const [chatMessage, setChatMessage] = useState('');

    const handleChange = (e) => setChatMessage(e.target.value);

    const handleSubmit = (e) => {
        e.preventDefault();

        if (chatMessage) {            
            emitMessage(chatMessage, roomId)
            setChatMessage('');
        };

    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div className="card-footer input-group mt-2 rounded-pill bg-white">
                    <input
                        className='chat-input'
                        type='text'
                        value={chatMessage}
                        onChange={handleChange}
                    />
                    <input 
                        className="chat-input-button btn btn-outline-primary"
                        type="submit"
                        value="Send"
                        onClick={handleSubmit}
                    />
                </div>
            </form>
        </div>
    );
}
