import React, { useState } from 'react'
import { OverlayTrigger, Popover } from 'react-bootstrap'
import { Eject, EmojiSmile, EmojiExpressionless, EmojiDizzy } from 'react-bootstrap-icons'

export const ChatInput = ({ roomId, emitMessage, username }) => {

    const [chatMessage, setChatMessage] = useState('');

    const handleChange = (e) => {
        setChatMessage(e.target.value);       
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        if (chatMessage) {            
            emitMessage(chatMessage, roomId, username)
            setChatMessage('');
        };

    };

    const popover = (
        <Popover id="popover-emoji">
            <Popover.Title as="h4">Emojis</Popover.Title>
            <Popover.Content>
                <EmojiDizzy className="me-1" />
                <EmojiSmile className="me-1" />
                <EmojiExpressionless />
                {/* <i className="bi bi-emoji-smile me-1"></i>
                <i className="bi bi-emoji-expressionless me-1"></i>
                <i className="bi bi-emoji-dizzy me-1"></i> */}
                <i className="bi bi-emoji-angry me-1"></i>
                <i className="bi bi-emoji-laughing me-1"></i>
                <i className="bi bi-emoji-sunglasses me-1"></i>
                <i className="bi bi-emoji-wink me-1"></i>
                <i className="bi bi-emoji-smile-upside-down me-1"></i>
                <i className="bi bi-emoji-heart-eyes me-1"></i>
                <i className="bi bi-emoji-frown me-1"></i>
                <i className="bi bi-emoji-neutral"></i>
                <Eject />
            </Popover.Content>
        </Popover>
    );

    return (
        <div>
            <div className="text-muted mt-2 ms-2 fs-6 mb-0">say something...</div>
            <hr />
            <form onSubmit={handleSubmit}>
                <div className="input-group">
                    <OverlayTrigger trigger="click" placement="top" overlay={popover}>
                        <button className="btn emoji-popover" type="button">
                            <i className="bi bi-emoji-smile"></i>
                        </button>
                    </OverlayTrigger>                    
                    <input
                        className="chat-input form-control border-0 "
                        type="text"
                        value={chatMessage}
                        placeholder=""
                        onChange={handleChange}
                    />
                    <button 
                        className="chat-input-button btn text-muted"
                        type="submit"
                        // value="Send"
                        onClick={handleSubmit}
                    >
                        <i className="bi bi-cursor-fill"></i>
                    </button>
                </div>
            </form>
            <hr />
        </div>
    );
}
