import React, { useState } from 'react'

export const ChatInput = ({ roomId, emitMessage, username }) => {

    const [chatMessage, setChatMessage] = useState('');

    const handleChange = (e) => setChatMessage(e.target.value);

    const handleSubmit = (e) => {
        e.preventDefault();

        if (chatMessage) {            
            emitMessage(chatMessage, roomId, username)
            setChatMessage('');
        };

    };

    return (
        <div>
            <div className="text-muted p-2">say something...</div>
            <hr />
            <form onSubmit={handleSubmit}>
                <div className="input-group mt-2 bg-dark text-white">
                    <input
                        className='chat-input mx-auto border-0 bg-light'
                        type='text'
                        value={chatMessage}
                        placeholder='...'
                        onChange={handleChange}
                    />
                    <input 
                        className="chat-input-button btn text-white"
                        type="submit"
                        value="Send"
                        onClick={handleSubmit}
                    />
                </div>
            </form>
        </div>
    );
}
