import React, { useEffect, useState } from 'react'

export const ChatInput = ({ roomId, emitMessage, username }) => {

    const [chatMessage, setChatMessage] = useState('');

    useEffect(() => {
        const popover = new bootstrap.Popover(document.querySelector('.emoji-popover'), {
            container: 'body'
        })
    })

    const handleChange = (e) => {
        setChatMessage(e.target.value);       
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        if (chatMessage) {            
            emitMessage(chatMessage, roomId, username)
            setChatMessage('');
        };

    };

    return (
        <div>
            <div className="text-muted mt-2 ms-2 fs-6 mb-0">say something...</div>
            <hr />
            <form onSubmit={handleSubmit}>
                <div className="input-group">
                    <button className="btn emoji-popover" type="button" data-bs-container="body" data-bs-toggle="popover" data-bs-placement="top" data-bs-content="Top popover">
                        <i className="bi bi-emoji-smile"></i>
                    </button>
                    <input
                        className="chat-input form-control border-0 "
                        type="text"
                        value={chatMessage}
                        placeholder=""
                        onChange={handleChange}
                    />
                    <button 
                        className="chat-input-button btn text-muted"
                        type="submit"
                        // value="Send"
                        onClick={handleSubmit}
                    >
                        <i className="bi bi-cursor-fill"></i>
                    </button>
                </div>
            </form>
            <hr />
        </div>
    );
}
