import React, { useState } from 'react'
import { OverlayTrigger, Popover } from 'react-bootstrap'

import Picker from 'emoji-picker-react';

export const ChatInput = ({ roomId, emitMessage, username }) => {

    const [chatMessage, setChatMessage] = useState('');
    const [chosenEmoji, setChosenEmoji] = useState(null);

    const handleChange = (e) => {
        setChatMessage(e.target.value);       
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        if (chatMessage) {            
            emitMessage(chatMessage, roomId, username)
            setChatMessage('');
        };

    };

    const handleEmojiClick = (e, emojiObject) => {
        setChosenEmoji(emojiObject.emoji)
        setChatMessage(chatMessage + emojiObject)
    }

    const popover = (
        <Popover id="popover-emoji">
            <Picker onEmojiClick={handleEmojiClick} />               
        </Popover>
    );

    return (
        <div>
            <div className="text-muted mt-2 ms-2 fs-6 mb-0">say something...</div>
            <hr />
            <form onSubmit={handleSubmit}>
                <div className="input-group">
                    <OverlayTrigger trigger="click" placement="top" overlay={popover}>
                        <button className="btn emoji-popover" type="button">
                            <i className="bi bi-emoji-smile"></i>
                        </button>
                    </OverlayTrigger>                    
                    <input
                        className="chat-input form-control border-0 "
                        type="text"
                        value={chatMessage}
                        placeholder=""
                        onChange={handleChange}
                    />
                    <button 
                        className="chat-input-button btn text-muted"
                        type="submit"
                        // value="Send"
                        onClick={handleSubmit}
                    >
                        <i className="bi bi-cursor-fill"></i>
                    </button>
                </div>
            </form>
            <hr />
        </div>
    );
}
