import React, { useState } from 'react'
import { useDispatch } from 'react-redux'
import { newRoom } from './roomsSlice'

export function RoomsList({ renderedRooms }) {
    const [clicked, setClicked] = useState(false)
    const [newRoomName, setNewRoomName] = useState('') 
    const dispatch = useDispatch()

    const handleClick = () => {
        setClicked(true)
    }

    const handleChange = (e) => {
        setNewRoomName(e.target.value)
    }

    const submitForm = () => {
        if (newRoomName) {
            dispatch(newRoom({
                roomName: newRoomName
            }))
        }
    }

    const displayRoomInput = (
            <form onSubmit={submitForm}>
                <div className="input-group">
                    <input 
                        className="form-control" 
                        type="text" 
                        onChange={handleChange}
                    />
                </div>
            </form>
    )

    const displayCreateRoomButton = (
        <tr>
            <td>
                <div 
                    className="d-flex btn fw-bold"
                    onClick={handleClick}
                >
                    <div>Create a room</div>
                    <div className="ms-2 fw-bold"><i className="bi bi-plus"></i></div>
                </div>          
            </td>
        </tr>
    )

    return (
        <div className="rooms-list bg-light mt-2">
            <table className="table">
                <tbody>
                    {renderedRooms}
                    {clicked ?
                        displayRoomInput :
                        displayCreateRoomButton
                    }                    
                </tbody>
            </table>            
        </div>
    )
}
