import React, { useEffect } from 'react'
import { useSelector } from 'react-redux'



export function ChatMessages({ roomId }) {

    // useEffect(() => {
    //     socket = io(endpoint)
    // });

    const rooms = useSelector(state => state.rooms.rooms)

    const room = rooms.find(room => room.id === roomId)
    const roomChatMessages = room.messages

    const renderedMessages = roomChatMessages.map(message => (
        <div className="list-group-item" key={message.id}>
            {message.text}
        </div>
    ))

    return (
        <div className="card-body list-group mt-2 chat-messages">
            {renderedMessages}
        </div>
    )
}
