import { nanoid } from '@reduxjs/toolkit'
import React, { useState } from 'react'
import { Table } from 'react-bootstrap'

export function RoomsList({ renderedRooms, createNewRoom, username }) {
    const [clicked, setClicked] = useState(false)
    const [newRoomName, setNewRoomName] = useState('') 

    const handleClick = () => {
        setClicked(true)
    }

    const handleChange = (e) => {
        setNewRoomName(e.target.value);
    }

    const submitForm = (e) => {
        e.preventDefault();
        if (newRoomName) {
            const roomId = nanoid()

            createNewRoom(newRoomName, roomId, username);
        }
        setNewRoomName('')
    }

    const displayRoomInput = (
        <tr>
            <td>
                <div className="">
                    <form onSubmit={submitForm}>
                        <div className="input-group">
                            <input 
                                className="form-control" 
                                type="text" 
                                value={newRoomName}
                                placeholder="Choose a name ..."
                                onChange={handleChange}
                            />
                        </div>
                    </form>
                </div>
            </td>
        </tr>        
    )

    const displayCreateRoomButton = (
        <tr>
            <th>
                <div className="d-flex btn fw-bold" onClick={handleClick}>
                    <div className="text-muted">Create a room</div>
                    <div className="ms-2 fw-bold">
                        <i className="bi bi-plus"></i>
                    </div>
                </div>          
            </th>
        </tr>
    )

    return (
        <div className="rooms-list overflow-auto bg-light mt-2">
            <Table size="sm">
                <thead>
                    {clicked ?
                        displayRoomInput :
                        displayCreateRoomButton
                    }

                </thead>
                <tbody>
                    <tr key={nanoid()}>
                        <td>
                            <div className="fw-bold p-2">Active Rooms</div>  
                        </td>
                    </tr>
                    {renderedRooms}

                </tbody>
            </Table>            
        </div>
    )
}
